import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.css';
import { Text, StyleSheet } from 'react-native';

class DisplayError extends Component{

  constructor(props){ //any data passed to a class object is stored inside props , to receive it i need props in the constructor and super parameters
    super(props)
    console.log(this.props.first.first.first[0])
    this.state={
      first: this.props.first.first.first[0]
    }
  }

  render(){
    const error_code = this.state.first.error_code
    const error_type = this.state.first.error_type
    const error_description = this.state.first.error_description
    const robot_tags = this.state.first.robot_tags
    return (
      <div>
      <label> {error_code}</label>
      <Text style={styles.baseText}>
        <Text style={styles.titleText} onPress={this.onPressTitle}>
          Error Code
        </Text>
        <Text numberOfLines={5}>
          {error_code}
        </Text>
      </Text>
      </div>
    )
  }
}
const styles = StyleSheet.create({
  baseText: {
    fontFamily: 'Cochin',
  },
  titleText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
});
export default DisplayError
