import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';

class App extends Component { //function App() {
  constructor(){
    super();
    this.state = {
      title: 'CorErrorClassification',
      first: []
    }
  }

  addRow(event){
    event.preventDefault();
    // console.log('in addRow method');
    let data ={
      s_num: this.refs.s_num.value ,
      error_type: this.refs.error_type.value,
      error_code: this.refs.error_code.value,
      error_description: this.refs

    }
  }
  render() {
    let title = this.state.title;
    return (
      <div className='App'>
      <h1> {title} </h1>
      <form>
      <row>
      <div class="col-2">
      <input type='text' refs='s_num' class= 'form-control' placeholder="S_NUM"/><br/>
      </div>
      </row>
      <row>
      <div class="col-2">
      <input type='text' refs='erro_type' class= 'form-control' placeholder="Error Type"/><br/>
      </div>
      </row>
      <row>
      <div class="col-2">
      <input type='text' refs='error_code' class= 'form-control' placeholder="Error Code"/><br/>
      </div>
      </row>
      <row>
      <div class="col-2">
      <textarea refs='error_description' class= 'form-control' placeholder="Error Description"/><br/>
      </div>
      </row>
      <row>
      <div class="col-2">
      <input  type='text' refs='robot_tags' class= 'form-control' placeholder="Robot Tags"/><br/>
      </div>
      </row>
      <row>
      <div class="col-2">
      <input type='text' refs='resolution' class= 'form-control' placeholder="Resolution"/><br/>
      </div>
      </row>
      <row><div class='col-2'>
        <button onClick={this.addRow.bind(this)}>Add Row </button>
      </div></row>
      </form>
      </div>
    );
  }
}

export default App;
