import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';

class Delete extends Component { //function App() {
  constructor(){
    super();
    this.state = {
      showAlert: false,
      first: []
    }
  }
  deleteRow(event){
    event.preventDefault();
    let data = {
      error_code: this.refs.error_code_delete.value
    };
    var request = new Request('http://localhost:3000/public/delete_row', {
      method: 'DELETE',
      headers: new Headers({'Content-Type': 'application/json' , 'Accept': 'application/json'}),
      body: JSON.stringify(data)
    });
    //xmlHTTPrequest
    fetch(request).then(function(response){
      response.json().then(function(data){
      });
    });
  }
  handleAlert(event){
    event.preventDefault();
    console.log('handleAlert')
    return
  }
  render() {
    return(
      <div>
      <h1> Delete </h1>
      <form>
            <div className="d-inline-flex p-2">
            <input type='text' ref="error_code_delete" className= 'form-control' placeholder="Error Code"/><br/>
            </div>
            <button onClick={(event) =>{this.deleteRow.bind(this); }}> Delete Record </button>
      </form>
      </div>
    );
  }
}

export default Delete;
