import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';

class Delete extends Component { //function App() {
  constructor(){
    super();
    this.state = {
      title: 'CorErrorClassification',
      first: []
    }
  }
  deleteRow(){
    console.log('updateRow entered');
    event.preventDefault();
    let data = {
      s_num: this.refs.s_num_update.value ,
      error_type: this.refs.error_type_update.value,
      error_code: this.refs.error_code_update.value,
      error_description: this.refs.error_description_update.value,
      robot_tags: this.refs.robot_tags_update.value,
    };
    var request = new Request('http://localhost:3000/public/update_row', {
      method: 'PATCH',
      headers: new Headers({'Content-Type': 'application/json' , 'Accept': 'application/json'}),
      body: JSON.stringify(data)
    });
    //xmlHTTPrequest
    fetch(request).then(function(response){
      response.json().then(function(data){
      });
    });
  }
  render() {
    return(
      <div>
      <h1> Delete </h1>
      <form>
            <div className="col-2">
            <input type='text' ref="s_num_delete" className= 'form-control' placeholder="S_NUM"/><br/>
            </div>
            <button onClick={this.deleteRow.bind(this)}> Delete Record </button>
      </form>
      </div>
    );
  }
}

export default Delete;
